import { BasicInput } from "./testComponents/BasicInput";
import { DropDownInput, Test } from "./testComponents/DropDownInput";
import styled from "styled-components";
import { useState } from "react";

const TestFormDiv = styled.div`
  display: flex;
  flex-direction: column;
  align-items: stretch;
  padding: 50px;
  background-color: pink;
`

function App() {
  const [values, setValues] = useState({
    username: "",
    email: "",
    birthday: "",
    password: "",
  });

  const [dropDownValues, setDropDownValues] = useState({
    color: "",
  })

  const onDropChange = (e) => {
    setDropDownValues({...values, [e.target.name]: e.target.value});
  }

  const onChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  const inputs = [
    {
      name: "username",
      Type: "text",
      PlaceHolder: "Username",
      ErrorMessage:
        "Username should be 3-16 characters and shouldn't include any special character!",
      LabelText: "Username",
      Pattern: "^[A-Za-z0-9]{3,16}$",
      Required: true,
    },
    {
      name: "email",
      Type: "email",
      PlaceHolder: "Email",
      ErrorMessage: "It should be a valid email address!",
      LabelText: "Email",
      Pattern: "^[A-Za-z0-9]*@[A-Za-z0-9]*.com",
      required: true,
    },
    {
      name: "birthday",
      Type: "date",
      PlaceHolder: "Birthday",
      LabelText: "Birthday",
    },
    {
      name: "password",
      Type: "password",
      PlaceHolder: "Password",
      ErrorMessage: "Password needs one special character",
      LabelText: "Password",
      required: true,
    },
  ];

  const inputs2 = [
    {
      name: "color",
      Label: "Color",
      Options: ["Red", "Blue", "Green"]
    }
  ]

  return (
    <TestFormDiv>
      {/*inputs.map((input, key) => (
        //{console.log(input)}
        <BasicInput key ={key} {...input} onChange = {onChange} value = {input[input.name]}/>
        //<Test key={key} {...input}></Test>
      ))*/}
      {inputs2.map((input, key) => (
        <DropDownInput key = {key} {...input} onChange = {onDropChange} value = {input[input.name]}></DropDownInput>
      ))}
    </TestFormDiv>
  );
}

export default App;
