import { BasicInput } from "./testComponents/BasicInput";
import { Test } from "./testComponents/Test";
import styled from "styled-components";
import { useState } from "react";

const TestFormDiv = styled.div`
  display: flex;
  flex-direction: column;
  align-items: stretch;
  padding: 10vh;
  background-color: pink;
`

function App() {
  const [values, setValues] = useState({
    username: "",
    email: "",
    birthday: "",
    password: "",
    confirmPassword: "",
  });

  const onChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  const inputs = [
    {
      name: "username",
      Type: "text",
      PlaceHolder: "Username",
      ErrorMessage:
        "Username should be 3-16 characters and shouldn't include any special character!",
      LabelText: "Username",
      Pattern: "^[A-Za-z0-9]{3,16}$",
      Required: true,
    },
    {
      name: "email",
      Type: "email",
      PlaceHolder: "Email",
      ErrorMessage: "It should be a valid email address!",
      LabelText: "Email",
      required: true,
    },
    {
      name: "birthday",
      Type: "date",
      PlaceHolder: "Birthday",
      LabelText: "Birthday",
    }
  ];

  return (
    <TestFormDiv>
      {inputs.map((input, key) => (
        //{console.log(input)}
        <BasicInput key ={key} {...input} onChange = {onChange} value = {input[input.name]}/>
        //<Test key={key} {...input}></Test>
      ))}
    </TestFormDiv>
  );
}

export default App;
